mapper.select:
	
		SELECT * 
		FROM(
			SELECT ROWNUM RN, seq, ReportDate, EndDate, UserId
			FROM(
				SELECT * 
				FROM  UserBlackList 
				WHERE EndDate
				like sysdate
		ORDER BY seq ASC
		) 
			
    		WHERE ROWNUM <= #{pageNum} * #{amount}
    	) 
  		
  		WHERE RN > (#{pageNum} - 1) * #{amount}

	
		SELECT * 
		FROM(
			SELECT ROWNUM RN, seq, ReportDate, EndDate, UserId
			FROM(
				SELECT * 
				FROM  UserBlackList 
				WHERE 1=1
				
		ORDER BY seq ASC
		) 
			
    		WHERE ROWNUM <= #{pageNum} * #{amount}
    	) 
  		
  		WHERE RN > (#{pageNum} - 1) * #{amount}
