mapper.insert:
 INSERT INTO  reviewboard(bseq, boardnum, title, nickname,  content, filename, like_num, unlike_num, cnt, UserId,writedate, moviecode, contenttype)
VALUES (#{bseq}, #{boardnum}, #{title},#{nickname}, #{content},#{filename}
, 0, 0, 0, #{userId},sysdate, #{moviecode}, #{contentType})

mapper.update:

	UPDATE REVIEWBOARD SET
		TITLE = #{title}, 
		CONTENT = #{content},
		moviecode=#{moviecode},
		contentType = #{contentType}
	WHERE BSEQ = #{bseq}

mapper.select:

	SELECT * 
	FROM ReviewCntHistory 
	WHERE BSEQ = #{bseq} AND USERID = #{userId}

SELECT * FROM( SELECT ROWNUM RN, BSEQ, BOARDNUM, TITLE, NICKNAME, CONTENT,WRITEDATE, FILENAME, LIKE_NUM, UNLIKE_NUM, CNT, USERID, MOVIECODE, CONTENTTYPE FROM( SELECT * FROM REVIEWBOARD WHERE boardnum = #{boardnum} and report = 'N'
AND TITLE LIKE '%'||#{searchKeyword}||'%'
AND CONTENT LIKE '%'||#{searchKeyword}||'%'
ORDER BY BSEQ DESC )
WHERE ROWNUM <= #{pageNum} * #{amount}
)
WHERE RN > (#{pageNum} - 1) * #{amount}

		SELECT * 
		FROM(
			SELECT ROWNUM RN, BSEQ, BOARDNUM, TITLE, NICKNAME, CONTENT,FILENAME,WRITEDATE, LIKE_NUM, UNLIKE_NUM, CNT, USERID 
			FROM(
				SELECT * 
				FROM REVIEWBOARD 
				WHERE 1=1 and report = 'N'
				AND TITLE LIKE '%'||#{searchKeyword}||'%'
		ORDER BY BSEQ DESC
		) 
			
    		WHERE ROWNUM <= #{pageNum} * #{amount}
    		
    	) 
  		
  		WHERE RN > (#{pageNum} - 1) * #{amount}

SELECT BSEQ, TITLE, NICKNAME, CONTENT,FILENAME,LIKE_NUM, UNLIKE_NUM, CNT FROM ( SELECT ROWNUM RN, BSEQ, TITLE, NICKNAME, CONTENT,LIKE_NUM, UNLIKE_NUM, FILENAME, CNT FROM ( SELECT * FROM REVIEWBOARD WHERE boardnum = 1 and report = 'N'
AND TITLE LIKE '%'||#{searchKeyword}||'%'
AND CONTENT LIKE '%'||#{searchKeyword}||'%'
ORDER BY BSEQ DESC )
WHERE ROWNUM <= #{pageNum} * #{amount}
)
WHERE RN > (#{pageNum} - 1) * #{amount}
