mapper.select:
SELECT user_id, user_pw, user_name, user_tel, user_email, user_birth, user_img
FROM member
WHERE user_id = #{userId}

mapper.update:
UPDATE member
SET user_id = #{userId}, user_pw = #{userPw}, user_name = #{userName}
WHERE user_id = #{userId}

UPDATE member
SET user_img = #{userImg}
WHERE user_id = #{userId}
